<script setup>
import Config from "../composables/Config";
import ConfigStore from "../store/ConfigStore";
import One from "../widget/Workspace/Add/Steps/One";

const cn = new Config();
const department_section = "layouts/default/navbar/right/department";
const workspaces = ref(null);
const actived_navbar = ref("department");
const active_section = ref("keyword-research");
const selected_tools_section = ref("create_content");

const auth = ref({
  name: "",
  wallet: 0,
  subscription: "",
});

onBeforeMount(() => {
  auth.value = {
    name: ConfigStore.user().name,
    wallet: ConfigStore.wallets()[0].balance ?? 0,
    subscription: cn.by_route(`constants/plans/${ConfigStore.plan().plan.name}`) + " " + cn.by_route(`constants/packages/${ConfigStore.plan().plan.package}`),
  }

  workspaces.value = ConfigStore.workspaces();
})

function add_workspace() {
  change_active_section("add_workspace");
  let popup = One.render("page");
  document.getElementById("page").appendChild(popup);
}

function change_selected_tools_section(title) {
  selected_tools_section.value =
    title === selected_tools_section.value ? "" : title;
};

function change_active_section(section) {
  active_section.value = section;
}
function profile() {
  return auth.value
}
</script>